      opacity: {
        get: function () {
          return this.uniforms.opacity.value;
        },
        set: function (value) {
          this.uniforms.opacity.value = value;
        }
      },
      color: {
        get: function () {
          return this.uniforms.diffuse.value;
        }
      }
    });
