const easeInExpo = x => x === 0 ? 0 : Math.pow(2, 10 * x - 10);

function Backdrop({
  children,
  floor = 0.25,
  segments = 20,
  receiveShadow,
  ...props
}) {
  const ref = React.useRef(null);
  React.useLayoutEffect(() => {
    let i = 0;
    const offset = segments / segments / 2;
